function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const history = useHistory(); // Initialize useHistory hook

  const handleLogin = (username, password) => {
    if (username === 'admin' && password === 'admin') {
      setIsLoggedIn(true);
      history.push('/dashboard'); // Redirect to Dashboard upon successful login
    } else {
      alert('Invalid username or password');
    }
  };


  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>My App</h1>
        </header>
        <Route exact path="/">
          {/* If user is logged in, redirect to Dashboard, otherwise show LoginForm */}
          {isLoggedIn ? <Redirect to="/dashboard" /> : <LoginForm onLogin={handleLogin} />}
        </Route>
        <Route path="/dashboard">
          {/* If user is not logged in, redirect to LoginForm, otherwise show Dashboard */}
          {isLoggedIn ? <Dashboard /> : <Redirect to="/" />}
        </Route>
      </div>
    </Router>
  );
}

export default App;
