function App() {
  const [isLoggedIn, setIsLoggedIn] = useState(false);

  const handleLogin = (username, password) => {
    if (username === 'admin' && password === 'admin') {
      setIsLoggedIn(true);
    } else {
      alert('Invalid username or password');
    }
  };

  return (
    <Router>
      <div className="App">
        <header className="App-header">
          <h1>My App</h1>
        </header>
        <Switch>
          <Route exact path="/">
            {/* Display login form if not logged in, otherwise redirect to Dashboard */}
            {!isLoggedIn ? <LoginForm onLogin={handleLogin} /> : <Redirect to="/dashboard" />}
          </Route>
          <Route path="/dashboard">
            {/* Display Dashboard if logged in, otherwise redirect to login page */}
            {isLoggedIn ? <Dashboard /> : <Redirect to="/" />}
          </Route>
        </Switch>
      </div>
    </Router>
  );
}

export default App;
